import React, { useState, useEffect, useRef } from 'react';
import styled, { css } from 'styled-components';

function ChatRoom({ socket, username }) {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [typingUsers, setTypingUsers] = useState(new Set());
  const [onlineUsers, setOnlineUsers] = useState([]);
  const [showUserList, setShowUserList] = useState(false);
  const messagesEndRef = useRef(null);
  const [userStatuses, setUserStatuses] = useState([]);

  useEffect(() => {
    socket.on('messageHistory', (history) => {
      setMessages(history);
    });

    socket.on('message', (message) => {
      setMessages((prev) => [...prev, message]);
    });

    socket.on('userList', (users) => {
      console.log('Received updated user list:', users);
      setOnlineUsers(users || []);
    });

    socket.emit('requestUserList');

    socket.on('userTyping', ({ user, isTyping }) => {
      setTypingUsers((prev) => {
        const newSet = new Set(prev);
        if (isTyping) {
          newSet.add(user);
        } else {
          newSet.delete(user);
        }
        return newSet;
      });
    });

    socket.on('userStatuses', (statuses) => {
      setUserStatuses(statuses);
    });

    return () => {
      socket.off('messageHistory');
      socket.off('message');
      socket.off('userList');
      socket.off('userTyping');
      socket.off('userStatuses');
    };
  }, [socket]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSend = (e) => {
    e.preventDefault();
    if (newMessage.trim()) {
      socket.emit('message', newMessage);
      setNewMessage('');
    }
  };

  const handleTyping = (e) => {
    setNewMessage(e.target.value);
    socket.emit('typing', e.target.value.length > 0);
  };

  const groupMessagesByDate = (messages) => {
    const groups = {};
    messages.forEach(msg => {
      const date = new Date(msg.time);
      const dateKey = date.toDateString();
      if (!groups[dateKey]) {
        groups[dateKey] = [];
      }
      groups[dateKey].push(msg);
    });
    return groups;
  };

  const formatMessageTime = (time) => {
    const date = new Date(time);
    const now = new Date();
    const yesterday = new Date(now);
    yesterday.setDate(yesterday.getDate() - 1);

    if (date.toDateString() === now.toDateString()) {
      return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
    } else if (date.toDateString() === yesterday.toDateString()) {
      return 'Yesterday ' + date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
    } else {
      return date.toLocaleDateString([], { 
        month: 'short', 
        day: 'numeric' 
      }) + ' ' + date.toLocaleTimeString([], { 
        hour: '2-digit', 
        minute: '2-digit' 
      });
    }
  };

  return (
    <ChatContainer>
      <MobileToggle onClick={() => setShowUserList(!showUserList)}>
        {showUserList ? 'âœ•' : 'ðŸ‘¥'} {!showUserList && `(${onlineUsers.length})`}
      </MobileToggle>

      <OnlineUsers show={showUserList}>
        <OnlineUsersHeader>
          <h3>Online Users ({onlineUsers.length})</h3>
          <CloseButton onClick={() => setShowUserList(false)}>âœ•</CloseButton>
        </OnlineUsersHeader>
        {onlineUsers.map((user, index) => {
          const userStatus = userStatuses.find(s => s.username === user);
          const isTyping = Array.from(typingUsers).includes(user);
          
          return (
            <UserItem 
              key={index} 
              isCurrentUser={user === username}
              status={userStatus?.status || 'offline'}
              isTyping={isTyping}
            >
              {user} {user === username ? '(You)' : ''}
            </UserItem>
          );
        })}
      </OnlineUsers>

      <ChatMain>
        <MessagesContainer>
          {Object.entries(groupMessagesByDate(messages)).map(([date, dateMessages]) => (
            <MessageGroup key={date}>
              <DateDivider>
                <DateLabel>
                  {date === new Date().toDateString() ? 'Today' : 
                   date === new Date(Date.now() - 86400000).toDateString() ? 'Yesterday' : 
                   new Date(date).toLocaleDateString([], { 
                     weekday: 'long', 
                     month: 'long', 
                     day: 'numeric' 
                   })}
                </DateLabel>
              </DateDivider>
              {dateMessages.map((msg, index) => (
                <MessageWrapper key={index}>
                  {(!dateMessages[index - 1] || 
                    dateMessages[index - 1].user !== msg.user) && (
                    <Username isOwn={msg.user === username}>{msg.user}</Username>
                  )}
                  <Message isOwn={msg.user === username}>
                    <MessageContent isOwn={msg.user === username}>
                      <MessageText>{msg.text}</MessageText>
                      <TimeStamp isOwn={msg.user === username}>
                        {formatMessageTime(msg.time)}
                      </TimeStamp>
                    </MessageContent>
                  </Message>
                </MessageWrapper>
              ))}
            </MessageGroup>
          ))}
          <div ref={messagesEndRef} />
        </MessagesContainer>

        {typingUsers.size > 0 && (
          <TypingIndicator>
            {Array.from(typingUsers).join(', ')} is typing...
          </TypingIndicator>
        )}

        <MessageForm onSubmit={handleSend}>
          <Input
            value={newMessage}
            onChange={handleTyping}
            placeholder="Type a message..."
          />
          <Button type="submit">Send</Button>
        </MessageForm>
      </ChatMain>
    </ChatContainer>
  );
}

