import React, { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';

function ChatRoom({ socket, username }) {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [typingUsers, setTypingUsers] = useState(new Set());
  const [onlineUsers, setOnlineUsers] = useState([]);
  const [showUserList, setShowUserList] = useState(false);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    socket.on('messageHistory', (history) => {
      setMessages(history);
    });

    socket.on('message', (message) => {
      setMessages((prev) => [...prev, message]);
    });

    socket.on('userList', (users) => {
      console.log('Received updated user list:', users);
      setOnlineUsers(users || []);
    });

    socket.emit('requestUserList');

    socket.on('userTyping', ({ user, isTyping }) => {
      setTypingUsers((prev) => {
        const newSet = new Set(prev);
        if (isTyping) {
          newSet.add(user);
        } else {
          newSet.delete(user);
        }
        return newSet;
      });
    });

    return () => {
      socket.off('messageHistory');
      socket.off('message');
      socket.off('userList');
      socket.off('userTyping');
    };
  }, [socket]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSend = (e) => {
    e.preventDefault();
    if (newMessage.trim()) {
      socket.emit('message', newMessage);
      setNewMessage('');
    }
  };

  const handleTyping = (e) => {
    setNewMessage(e.target.value);
    socket.emit('typing', e.target.value.length > 0);
  };

  return (
    <ChatContainer>
      <MobileToggle onClick={() => setShowUserList(!showUserList)}>
        {showUserList ? 'âœ•' : 'ðŸ‘¥'} {!showUserList && `(${onlineUsers.length})`}
      </MobileToggle>

      <OnlineUsers show={showUserList}>
        <OnlineUsersHeader>
          <h3>Online Users ({onlineUsers.length})</h3>
          <CloseButton onClick={() => setShowUserList(false)}>âœ•</CloseButton>
        </OnlineUsersHeader>
        {onlineUsers.map((user, index) => (
          <UserItem key={index} isCurrentUser={user === username}>
            {user} {user === username ? '(You)' : ''}
          </UserItem>
        ))}
      </OnlineUsers>

      <ChatMain>
        <MessagesContainer>
          {messages.map((msg, index) => (
            <Message key={index} isOwn={msg.user === username}>
              <MessageContent isOwn={msg.user === username}>
                <Username isOwn={msg.user === username}>{msg.user}</Username>
                <MessageText>{msg.text}</MessageText>
                <TimeStamp isOwn={msg.user === username}>
                  {new Date(msg.time).toLocaleTimeString()}
                </TimeStamp>
              </MessageContent>
            </Message>
          ))}
          <div ref={messagesEndRef} />
        </MessagesContainer>

        {typingUsers.size > 0 && (
          <TypingIndicator>
            {Array.from(typingUsers).join(', ')} is typing...
          </TypingIndicator>
        )}

        <MessageForm onSubmit={handleSend}>
          <Input
            value={newMessage}
            onChange={handleTyping}
            placeholder="Type a message..."
          />
          <Button type="submit">Send</Button>
        </MessageForm>
      </ChatMain>
    </ChatContainer>
  );
}

const ChatContainer = styled.div`
  display: flex;
  width: 95%;
  max-width: 1400px;
  height: 88vh;
  background: #ffffff;
  border-radius: 20px;
  box-shadow: 0 12px 32px rgba(0, 0, 0, 0.12);
  overflow: hidden;
  position: relative;
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.2);

  @media (max-width: 768px) {
    width: 100%;
    height: 100vh;
    border-radius: 0;
  }
`;

const MobileToggle = styled.button`
  display: none;
  position: absolute;
  top: 1.2rem;
  left: 1.2rem;
  z-index: 1000;
  padding: 0.6rem;
  background: #4a90e2;
  color: white;
  border: none;
  border-radius: 50%;
  cursor: pointer;
  font-size: 1.2rem;
  width: 40px;
  height: 40px;
  display: flex;
  align-items: center;
  justify-content: center;
  box-shadow: 0 2px 8px rgba(74, 144, 226, 0.3);
  transition: transform 0.2s ease;

  &:hover {
    transform: scale(1.05);
  }

  @media (max-width: 768px) {
    display: flex;
  }
`;

const OnlineUsers = styled.div`
  width: 280px;
  padding: 1.5rem;
  background: #f8fafc;
  overflow-y: auto;
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
  border-right: 1px solid rgba(0, 0, 0, 0.08);
  position: relative;

  &::after {
    content: '';
    position: absolute;
    top: 0;
    right: 0;
    width: 1px;
    height: 100%;
    background: linear-gradient(to bottom, 
      rgba(255,255,255,0) 0%,
      rgba(255,255,255,0.8) 50%,
      rgba(255,255,255,0) 100%);
  }

  @media (max-width: 768px) {
    position: absolute;
    left: ${props => props.show ? '0' : '-100%'};
    top: 0;
    bottom: 0;
    width: 85%;
    max-width: 320px;
    z-index: 100;
    transition: left 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    box-shadow: 2px 0 12px rgba(0, 0, 0, 0.1);
    background: white;
  }
